DROP TABLE IF EXISTS `p2p_platform`;
CREATE TABLE `p2p_platform` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) NOT NULL DEFAULT '' COMMENT '简称',
  `plat_name` varchar(50) DEFAULT NULL COMMENT '平台名称',
  `platform_intro` varchar(2000) DEFAULT NULL COMMENT '平台介绍TEXT',
  `platform_img` varchar(200) DEFAULT NULL COMMENT '平台图标',
  `platform_tags` varchar(255) DEFAULT '' COMMENT '平台特色TAG',
  `risk` char(1) DEFAULT NULL COMMENT '平台分级',
  `riskDisplay` varchar(20) DEFAULT NULL COMMENT '分级名称',
  `finance_bg` varchar(30) DEFAULT NULL COMMENT '融资背景',
  `platform_bg` varchar(30) DEFAULT NULL COMMENT '平台背景',
  `bank_manage` varchar(50) DEFAULT NULL COMMENT '银行存管',
  `icp_card` varchar(30) DEFAULT NULL COMMENT 'ICP证',
  `cash_speed` varchar(50) DEFAULT NULL COMMENT '提现速度',
  `principal` varchar(10) DEFAULT NULL COMMENT '注册资本',
  `online` varchar(50) DEFAULT NULL COMMENT '上线时间',
  `status` char(1) NOT NULL DEFAULT '1' COMMENT '平台状态',
  `url` varchar(100) DEFAULT NULL COMMENT '网址',
  `remark` varchar(4000) DEFAULT '',
  `create_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `update_time` timestamp NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=234 DEFAULT CHARSET=utf8;

