-- ----------------------------
--  Table structure for `p2p_post_order`
-- ----------------------------
DROP TABLE IF EXISTS `p2p_post_order`;
CREATE TABLE `p2p_post_order` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `account` char(11) NOT NULL DEFAULT '',
  `investor_name` varchar(50) NOT NULL DEFAULT '' COMMENT '投资人',
  `investor_phone` char(11) DEFAULT NULL COMMENT '投资人手机',
  `invest_date` date DEFAULT NULL COMMENT '投资日期',
  `deposit` double(15,2) NOT NULL DEFAULT '0.00' COMMENT '投资金额',
  `platform_id` int(11) DEFAULT '0' COMMENT '平台id',
  `platform_name` varchar(50) DEFAULT NULL COMMENT '平台名称',
  `logo` varchar(100) DEFAULT '' COMMENT 'logo图片地址',
  `url` varchar(100) DEFAULT '' COMMENT '网址',
  `activity_id` int(11) NOT NULL DEFAULT '0' COMMENT '活动id',
  `is_first` varchar(20) DEFAULT NULL COMMENT '首复投',
  `plan_id` int(11) NOT NULL DEFAULT '0' COMMENT '方案id',
  `plan_name` varchar(50) DEFAULT NULL COMMENT '方案名称',
  `plan_item` varchar(50) DEFAULT NULL COMMENT '方案投资项目',
  `deposit_range` varchar(100) DEFAULT NULL COMMENT '方案投资金额范围',
  `rebate` double(11,2) NOT NULL DEFAULT '0.00' COMMENT '返利比例',
  `cash_back` double(11,2) NOT NULL DEFAULT '0.00' COMMENT '返现',
  `add_rebate` double(11,2) NOT NULL DEFAULT '0.00' COMMENT '搭车满额奖励比例',
  `rebatePlus` double(11,2) DEFAULT '0.00' COMMENT '奖励金额',
  `rate` double(11,2) NOT NULL DEFAULT '0.00' COMMENT '年化利率',
  `interest` double(11,2) NOT NULL DEFAULT '0.00' COMMENT '投资收回利息',
  `redback` double(11,2) NOT NULL DEFAULT '0.00' COMMENT '红包',
  `bbin` double(11,2) NOT NULL DEFAULT '0.00' COMMENT '体验金',
  `bbin_interest` double(11,2) NOT NULL DEFAULT '0.00' COMMENT '体验金收益',
  `totalIncome` double(11,2) DEFAULT '0.00' COMMENT '总收益',
  `year_rate` double(11,2) DEFAULT '0.00' COMMENT '年化利率',
  `days` int(11) DEFAULT NULL COMMENT '周期',
  `received_date` date DEFAULT NULL COMMENT '回款日期',
  `post_date` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '交单时间',
  `update_date` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `username` varchar(30) DEFAULT '' COMMENT '投资平台用户名',
  `img` varchar(255) DEFAULT '' COMMENT '投资平台截图',
  `status` char(1) DEFAULT '0' COMMENT '状态(0:审核中;1:成功;2:失败;3:虚假单)',
  `remark` varchar(255) DEFAULT '' COMMENT '备注原因',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='交单信息记录';

